var canvasStream;
var contextStream;
var canvasImg;
var contextImg;
var streamImg = new Image();
var detectedImg = new Image();

var running = false;
var streaming = false;
var ws; 
var wsUri = "ws:";
var loc = window.location;

if (loc.protocol === "https:") { wsUri = "wss:"; }
// This needs to point to the web socket in the Node-RED flow
wsUri += "//" + loc.host + loc.pathname.replace("app","ws/streaming");

window.onbeforeunload = function() {
    if (ws) ws.close();
};

function onLoad() {
    $("#streamButton").hide();
    $("#startStopButton").text("Start camera");

    canvasStream = document.getElementById('canvas-video');
    contextStream = canvasStream.getContext('2d');

    canvasImg = document.getElementById('canvas-image');
    contextImg = canvasImg.getContext('2d');

    // show loading notice
    contextStream.fillStyle = '#333';
    contextStream.fillText('Loading...', canvasStream.width/2-30, canvasStream.height/3);

    getImagesCount();
}

function wsConnect() {
    ws = new WebSocket(wsUri);
    ws.onmessage = function(msg) {
        var base64String = msg.data;

        streamImg.onload = function () {
            contextStream.drawImage(this, 0, 0, canvasStream.width, canvasStream.height);
        };
        streamImg.src = 'data:image/jpeg;base64,' + base64String;
        $("#status").text("Connected @ " + new Date());
    };
    ws.onopen = function() {
        // update the status div with the connection status
        $("#status").text("Connected");
        $("#streamButton").text("Stop stream");
    };
    ws.onclose = function() {
        // update the status div with the connection status
        $("#status").text("Not connected");
        // in case of lost connection tries to reconnect every 3 secs
        setTimeout(function() {
            if (streaming == true)
                wsConnect();
        },3000);
    };
}

function startStopCamera() {
    if (running === false) {
        $.get("/start", function(data, status) {
            $("#startStopButton").text("Stop camera");
            $("#streamButton").text("Start stream");
            $("#status").text(data); // Started
            $("#streamButton").show();
            running = true;
        });
    } else {
        startStopStream();
        $.get("/stop", function(data, status) {
            $("#startStopButton").text("Start camera");
            $("#status").text(data); // Stopped
            $("#streamButton").hide();
            running = false;
        });
    }
}

function startStopStream() {
    if (running == false) {
        alert("Start camera first.");
        return;
    }

    if (streaming === false) {
        $("#status").text("connecting to stream...");
        wsConnect();
        streaming = true;
    } else {
        $("#streamButton").text("Start stream");
        if (ws) ws.close();
        streaming = false;
    }
}

// detected image
var index = 0;

function setImage(data) {
    detectedImg.onload = function () {
        contextImg.drawImage(this, 0, 0, canvasImg.width, canvasImg.height);
    };
    detectedImg.src = 'data:image/jpeg;base64,' + data;
}

function clearImage() {
    contextImg.clearRect(0, 0, canvasImg.width, canvasImg.height);

    contextImg.fillStyle = '#333';
    contextImg.fillText('No image...', canvasImg.width/2-30, canvasImg.height/3);
}

function onPrev() {
    if (index == 0)
        return;

    index--;
    getImageAt(index);
}

function onNext() {
    index++;
    getImageAt(index);
}

function getImageAt() {
    $.get("/image/" + index, function(data, status) {
        if (data && data.fileData != '') {
            setImage(data.fileData);
            $("#imagesMessages").text(data.fileName);
        } else {
            if (index > 0)
                index--;
            clearImage()
        }
    });
}

function onDelete() {
    $.ajax({
        url: "/image/" + index,
        type: "DELETE",
        success: function() {
            $("#imagesMessages").text("Deleted...");
            clearImage();
        }
    });
}

function onDeleteAll() {
    $.ajax({
        url: "/images",
        type: "DELETE",
        success: function() {
            $("#imagesMessages").text("All images deleted...");
            clearImage();
            index = 0;
        }
    });
}

function getImagesCount() {
    $.get("/images/metadata", function(data, status) {
        if (data && data.count > 0) {
            index = data.count - 1;
            getImageAt(index);
        }
    });
}

